import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid } from '@fortawesome/fontawesome-svg-core/import.macro' 


export const SideView = (props) => {
  const {elements} = props




      //closes Side Bar
      function closeNav() {
        document.getElementById("sideView").style.width = "0%";
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "0px";
        }
      }

      return (
<nav >
          {elements()}
            {/* {authUser ? ( */}


                            <React.Fragment>

                            <div id="mySideView" className="sideView">
                              <span style={{cursor: 'pointer'}} className="closebtn" onClick={closeNav}>&times;</span>
                              <Link className="nav" onClick={closeNav} to="/about">About Us</Link>
                              {/* <Link className="nav" onClick={closeNav} to="/signout" >Sign Out</Link> */}
                            </div>

                          </React.Fragment>
                          </nav>

      )
}

const styles = {
    but: {
        display: 'inline-flex',
        alignItems: "center",
        position: 'absolute',
        left: '3%',
        height: '40%',
        width: 'auto',
      },
}
