import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid } from '@fortawesome/fontawesome-svg-core/import.macro' 


export const SideView = (props) => {
  const {elements} = props

    //Opens Side Bar
    function openNav() {

        if (window.innerWidth <= 751) {
          document.getElementById("mySideView").style.width = "100%"; 
        } else {
          document.getElementById("mySideView").style.width = "25%";   
        } 
        const dropDownLinks = document.getElementById("mySideViw").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "25px";
        }
      }


      //closes Side Bar
      function closeNav() {
        document.getElementById("mySideViw").style.width = "0%";
        const dropDownLinks = document.getElementById("mySideViw").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "0px";
        }
      }

      return (

            {/* {authUser ? ( */}
              <React.Fragment>
                <button className="btn rounded-5 hoverLink linkDiv"  onClick={openNav}>{elements()}
                <div id="mySideViw" className="sideViw">
                  <span style={{cursor: 'pointer'}} className="closebtn" onClick={closeNav}>&times;</span>
                  <Link className="nav" onClick={closeNav} to="/about">About Us</Link>
                  {/* <Link className="nav" onClick={closeNav} to="/signout" >Sign Out</Link> */}
                </div>
                </button>


      )
}

const styles = {
    but: {
        display: 'inline-flex',
        alignItems: "center",
        position: 'absolute',
        left: '3%',
        height: '40%',
        width: 'auto',
      },
}
