import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { solid } from "@fortawesome/fontawesome-svg-core/import.macro"; // <-- import styles to be used
import { Link } from "react-router-dom";
import React, { useState } from 'react';

// import CheckOutForm from "../temps/CheckOutForm"
//form page
const CartDropDown = (props) => {

   const [truth,setTruth]= useState(false)
    const {clNav,opNav} = props
    const fullArr = JSON.parse(localStorage.getItem(('cart')|| []))
  const results = []
console.log(fullArr)
  function testCart() {
    const arr = fullArr.filter((obj) => obj.token === false )
    for (const obj of arr) {
            if (obj.token === false) {
                return setTruth(true)
            }
    }
    return setTruth(false)
  }

  function removeItem(event){
    event.preventDefault()
    window.location.reload()

    const e = event.target.parentNode.childNodes[2]
    const arr = fullArr.filter((obj) => obj.name !== e.textContent )

     console.log(e)
     console.log(arr)
     localStorage.setItem('cart', JSON.stringify(arr));
     console.log(JSON.parse(localStorage.getItem(('cart')|| [])))
  }
  //Opens Side Bar
  function openNav() {
      document.getElementById("mySideBuy").style.width = "100%";
    const dropDownLinks = document.getElementById("scrolls");
 dropDownLinks.style.display = "block";
 const buy = document.getElementById("buy");
 buy.style.display = "block";
 opNav()
  }
  //closes Side Bar
  function closeNav() {
    document.getElementById("mySideBuy").style.width = "0%";
    const dropDownLinks = document.getElementById("scrolls");
 dropDownLinks.style.display = "none";
 const buy = document.getElementById("buy");
 buy.style.display = "none";
 clNav()
    }

    for (const obj of fullArr) {
      results.push(
        <span
          className="btn rounded-5 hoverLink newLinkDiv"
          style={{ backgroundColor: "rgb(255 255 255)" }}
        >
          <img
            alt="merchandise"
            src={obj.img}
            style={{ position:'relative',height: "9.5vw", width: "7.5vw", right: '17vw' }}
          />
          <h3 className="price">{obj.price}</h3>
          <h1 className=" nameTitle">{obj.name}</h1>
          <button className="btn btn-outline-secondary rounded-circle" style={{width: "4.5vw", height: "4.5vw", position: 'relative', left:'12vw',bottom: '8vw',color: "#374e66", borderColor: "#374e66"}} onClick={removeItem}><FontAwesomeIcon  icon={solid("trash-can")}></FontAwesomeIcon></button>
        </span>
      );
  }
  console.log(results);
  console.log(fullArr);

  //the div and form of buttons
  return (
    <nav style={styles.but}>
      {/* {authUser ? ( */}
      <React.Fragment>
        <button
          type="button"
          class="hoverLink btn btn-light rounded-circle"
          style={{
            position: "absolute",
            textAlign: "center",
            left: "89vw",
            bottom: "28.5vw",
            color: "rgb(192 192 192)",
            cursor: "pointer",
            width: "3.5vw",
            height: "3.5vw",
          }}
          onClick={openNav,testCart()}
        >
          <FontAwesomeIcon style={{width: "1.5vw", height: "1.5vw"}} icon={solid("cart-shopping")}>
            {" "}
            <span class="position-absolute top-0 start-100 translate-middle p-2 bg-danger border border-light rounded-circle">
                1
              <span class="visually-hidden">New alerts</span>
            </span>
          </FontAwesomeIcon>
        </button>
        <div id="mySideBuy" className="sideBuy">

          <div id="scrolls" className="scroll">{results}</div>
          <div id="buy" className="buy">{truth ? <h1>yes</h1>: <h1>no</h1>}</div>
          <span
            style={{ cursor: "pointer" }}
            className="closebtn"
            onClick={closeNav}
          >
            &times;
          </span>
        </div>
      </React.Fragment>
    </nav>
  );
};

//styles
const styles = {
  but: {
    display: "inline-flex",
    alignItems: "center",
    position: "absolute",
    left: "3%",
    height: "40%",
    width: "auto",
  },
};

export default CartDropDown;
