import { SideView } from "./SideView";
//form page
const ListBuild = (props) => {
  const { fullArr} = props;
  const results = [];
    //Opens Side Bar
    function openNav() {

        if (window.innerWidth <= 751) {
          document.getElementById("mySideView").style.width = "100%"; 
        } else {
          document.getElementById("mySideView").style.width = "25%";   
        } 
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "25px";
        }
      }


  for (const arr of fullArr) {
  for (const obj of arr) {
      results.push(
        <button className="btn rounded-5 hoverLink linkDiv" onClick={openNav}>
            <img
            className=""
            alt="merchandise"
            src={obj.img}
            style={{ height: "9.5vw", width: "7.5vw" }}
          />
          <h3 className="price" >
            {obj.price}
          </h3>
          <h1 className=" nameTitle">
            {obj.name}
          </h1>
          </button>
      );
    }
  }
