import React from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { solid } from '@fortawesome/fontawesome-svg-core/import.macro' 


export const SideView = (props) => {
  const {elements} = props

    //Opens Side Bar
    function openNav() {

        if (window.innerWidth <= 751) {
          document.getElementById("mySideView").style.width = "100%"; 
        } else {
          document.getElementById("mySideView").style.width = "25%";   
        } 
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "25px";
        }
      }


      //closes Side Bar
      function closeNav() {
        document.getElementById("sideView").style.width = "0%";
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "0px";
        }
      }

      return (
        <button className="btn rounded-5 hoverLink linkDiv"  onClick={openNav}>{elements()}
            {/* {authUser ? ( */}

              </button>

      )
}

const styles = {
    but: {
        display: 'inline-flex',
        alignItems: "center",
        position: 'absolute',
        left: '3%',
        height: '40%',
        width: 'auto',
      },
}
