import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { solid } from "@fortawesome/fontawesome-svg-core/import.macro"; // <-- import styles to be used
import { SideView } from "./SideView";
import { Link } from 'react-router-dom';
//form page
const ListBuild = (props) => {
  const { fullArr} = props;
  const results = [];
    //Opens Side Bar
    function openNav() {

        if (window.innerWidth <= 751) {
          document.getElementById("mySideView").style.width = "100%"; 
        } else {
          document.getElementById("mySideView").style.width = "25%";   
        } 
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "25px";
        }
      }

      function closeNav() {
        document.getElementById("sideView").style.width = "0%";
        const dropDownLinks = document.getElementById("sideView").childNodes;
        for (let a in dropDownLinks) {
          if (a >= 1)
          dropDownLinks[a].style.fontSize = "0px";
        }
      }

  for (const arr of fullArr) {
  for (const obj of arr) {
      results.push(
        <button className="btn rounded-5 hoverLink linkDiv" onClick={openNav}>
            <img
            className=""
            alt="merchandise"
            src={obj.img}
            style={{ height: "9.5vw", width: "7.5vw" }}
          />
          <h3 className="price" >
            {obj.price}
          </h3>
          <h1 className=" nameTitle">
            {obj.name}
          </h1>
          </button>
      );
    }
  }
  console.log(results)
console.log(fullArr)


  //the div and form of buttons
  return (
    <div className="scroll">
{results}
<nav>
<React.Fragment>

    <div id="mySideView" className="sideView">
      <span style={{cursor: 'pointer'}} className="closebtn" onClick={closeNav}>&times;</span>
      <Link className="nav" onClick={closeNav} to="/about">About Us</Link>
      {/* <Link className="nav" onClick={closeNav} to="/signout" >Sign Out</Link> */}
    </div>

  </React.Fragment>
  </nav>
  </div>
  );
};

//styles
const styles = {};

export default ListBuild;
