import React, { useState, useEffect } from 'react';
import { cat } from "../Data/staticMarkFavData.json";


//form for details
const FormDetail = (props) => {


    const {
      cato,
      id,
        user,
        courseUserId,
        errors,
        addCart,
        handelReviews,
        name,
        price,
      } = props; 
      const [size, setSize] = useState(true)
      const [newSize, setNewSize] = useState(0)
      const [newPrice, setNewPrice] = useState(price)
      const [fullArr, setFullArr] = useState([[...cat.onePiece], [...cat.naruto], [...cat.bleach]])
      const [newName, setNewName] = useState(document.getElementsByClassName('titleName')[0].textContent)
      const [newCName, setNewCName] = useState(name)

console.log(document.getElementsByClassName('titleName')[0].innerHTML)

  //submit
  function handleAddCart(event) {
    event.preventDefault();
    addCart(id+newSize, newName, newCName, price );
  }


  function catData(id2) {
    if (cato === 'One Piece') {
     for (const obj of fullArr[0]) {
      if (id2 === obj.id) {
        console.log(obj)
        setNewName(`${obj.size} - ${obj.name}`)
      setNewPrice(obj.price)
        return obj
      }

     }

    } else if (cato === 'Naruto') {
        for (const obj of fullArr[1]) {
         if (id2 === obj.id) {
          console.log(obj)

          setNewName(`${obj.size} - ${obj.name}`)
          setNewPrice(obj.price)
           return obj
         }

        }
    } else {
        for (const obj of fullArr[2]) {
         if (id2 === obj.id) {
          console.log(obj)

          setNewPrice(obj.price)
          setNewName(`${obj.size} - ${obj.name}`)
           return obj
         }

        }
    }
  }



//cancel
  function handelReview(event) {
    event.preventDefault();
    setSize(size ? false : true)

    if (size) {

      setNewSize(3000)
      console.log(id + 3000)
     catData(id + 3000)


    } else {
      setNewSize(0)
      console.log(id + 0)
 catData(id + 0)
    }
    handelReviews(size)
  }


 //the div and form of buttons if user is auth user show update and delete button
    return (

     <div>
       <ErrorsDisplay errors={errors} />
       <form onSubmit={handleAddCart}>
       <button className="btn btn-outline-dark rounded-pill size" onClick={handelReview}>{size ? '50pcs' : '100pcs'}</button>
         <button className="btn btn-outline-dark rounded-pill" type="submit" style={{marginLeft: '.5vw'}}>Add To Cart +</button>
       </form>
     </div>
    )
}


//display errors
function ErrorsDisplay({ errors }) {
  let errorsDisplay = null;

  if (errors.length) {
    errorsDisplay = (
      <div>
        <h2 className="validation--errors--label">Validation errors</h2>
        <div className="validation-errors">
          <ul>
            {errors.map((error, i) => <li key={i}>{error}</li>)}
          </ul>
        </div>
      </div>
    );
  }

  return errorsDisplay;
}
//styles
const styles = {

}

export default FormDetail
