import React from 'react';

//form page
const FilterS = (props) => {
   function filter(event) {
    const val = document.getElementsByClassName("scroll").value
const e =  document.getElementsByClassName("scroll")[1].childNodes;
for (const elm of e) {
    if (elm.val === 0) {
elm.style.display = ''
    } else if (elm.val === val) {
        elm.style.display = 'block'
    } else {
        elm.style.display = 'none'
    }

}
console.log(e)
    }

    return (
        <div
        className="sho rounded-pill d-grid justify-content-center align-items-center grid-wrap"
        style={{
          width: "76vw",
          height: "13vh",
          backgroundColor: "rgb(35 52 72)",
        }}
      >
        <div
          className="sho rounded-pill d-flex justify-content-center align-items-center grid-wrap"
          style={{
            position: "relative",
            width: "74vw",
            backgroundColor: "rgb(76 104 133)",
            height: "10vh",
            bottom: 3,
          }}
        >

          <input className="sho rounded-pill" style={{color: '#f2ebeb',backgroundColor:'#797d99',border:'none', width: '30vw', position: 'relative', right: '14vw', top: '-.25vh'}}></input> 
<select id='filt' onInput={filter} style={{color: '#f2ebeb',backgroundColor:'#797d99',border:'none', width: 'auto', position: 'relative'}} className="sho rounded-pill">
    <option value='0'>All Items</option>
    <option value='1'>One Piece</option>
    <option value="2">Dragonball</option>
    <option value="3">Naruto</option>
    <option value="4">Bleach</option>
    <option value="5">Demon Slayer</option>
    <option value="6">My Hero Academy</option>
    <option value="7">One Punch Man</option>
    <option value="8">DeathNote</option>
    <option value="9">Braclets</option>
    <option value="10">Key Chains</option>
    <option value="11">Necklace</option>
    <option value="12">Lanyard</option>
</select>
      </div>
      </div>
    );
}

export default FilterS;
