import React, { Component } from "react";
import { cat } from "../Data/staticMarkData.json";
//got this from stack over flow Couldnt get text
import ReactMarkdown from "react-markdown";

class MarketPlace extends Component {
  //org state
  state = {
    id: "",
    name: "",
    stars: 4,
    price: 0,
    userId: "",
    courseUserId: "",
    user: "",
    hashPass: "",
    errors: [],
    full: [[...cat.nutrition], [...cat.athletics], [...cat.misc]],
  };

  //mounts after
  async componentDidMount() {
    const { full } = this.state;
    //var
    const { context } = this.props;
    const auth = context.authenticatedUser;
    const hashPass = context.hashPass;
    const id = window.location.pathname.split("/");
    console.log(id.length);
    console.log(Number(id[2]));
    for (const arr of full) {
      for (const obj of arr) {
        if (obj.id === Number(id[2])) {
            console.log(obj)
          this.setState({
            id: obj.id,
            name: obj.name,
            stars: obj.stars,
            price: obj.price,
          });

        }
      }
    }
  }

  //page
  render() {
    const { name, stars, price, userId, courseUserId, errors } = this.state;

    console.log(name);

    return (
      <div style={{ backgroundColor: "#001220" }} className="text-center">
        <div
          className="spacer layer3 d-grid justify-content-center align-items-center grid-wrap"
          style={{ margintop: "3em" }}
        >
          <span
            className="sho rounded-pill"
            style={{
              width: "35vw",
              backgroundColor: "#001220",
              position: "relative",
              right: ".8%",
              top: "-45.5%",
            }}
          >
            <h1 style={styles.title}>FANIMISS</h1>
          </span>
        </div>
        <div className="spacer layer1"></div>
        </div>
    );
  }

  //cancel to route
  cancel = () => {
    this.props.history.push("/courses");
  };
}

//styles

const styles = {};

export default MarketPlace;
