import React, { Component } from "react";
import { cat } from "../Data/staticMarkData.json";
//got this from stack over flow Couldnt get text
import ReactMarkdown from "react-markdown";
import FormDetail from '..//FormDetail';

class MarketPlace extends Component {
  //org state
  state = {
    id: "",
    name: "",
    stars: 4,
    price: 0,
    userId: "",
    courseUserId: "",
    user: "",
    hashPass: "",
    errors: [],
    full: [[...cat.nutrition], [...cat.athletics], [...cat.misc]],
  };

  //mounts after
  async componentDidMount() {
    const { full } = this.state;
    //var
    const { context } = this.props;
    const auth = context.authenticatedUser;
    const hashPass = context.hashPass;
    const id = window.location.pathname.split("/");
    console.log(id.length);
    console.log(Number(id[2]));
    for (const arr of full) {
      for (const obj of arr) {
        if (obj.id === Number(id[2])) {
            console.log(obj)
          this.setState({
            id: obj.id,
            name: obj.name,
            stars: obj.stars,
            price: obj.price,
          });

        }
      }
    }
  }

  //page
  render() {
    const { name, stars, price, userId, courseUserId, errors } = this.state;

    console.log(name);

    return (
      <div style={{ backgroundColor: "#001220"}} className="d-flex justify-content-center align-items-center flex-wrap">
        <div
          className="spacer layer3 d-grid justify-content-center align-items-center grid-wrap"
        >
          <span
            className="sho rounded-pill"
            style={{
              width: "35vw",
              backgroundColor: "#001220",
              position: "relative",
              right: ".8%",
              top: "-45.5%",
            }}
          >
            <h1 style={styles.title}>FANIMISS</h1>
          </span>
        </div>
        <div className="sho rounded-5 d-flex justify-content-center align-items-center flex-wrap" style={styles.cont}>
        <FormDetail
                        style={styles.forms}
                        errors={errors}
                        user={userId}
                        courseUserId={courseUserId}
                        update={this.update}
                        submit={this.submit}
                        cancel={this.cancel}
                        submitButtonText='Delete Course'
                        elements={() => (
                            <React.Fragment>
             <div className="sho rounded-5 d-flex justify-content-center align-items-center flex-wrap" style={styles.inn}>

            </div>
            </React.Fragment>
                                    )} />
        </div>
        <div className="spacer layer1"></div>
        </div>
    );
  }

  //cancel to route
  cancel = () => {
    this.props.history.push("/courses");
  };
}

//styles

const styles = {
    title: {
      backgroundImage:
        " url('https://media1.giphy.com/media/VOCwBp4kcegJ2rfCF7/giphy.gif?cid=790b7611802d3e1186bf825d92f34d30607f723a038efaee&rid=giphy.gif&ct=g')",
      backgroundPosition: "center center",
      backgroundRepeat: "no-repeat",
      backgroundSize: "100%",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent",
      fontFamily: "'Arimo', sans-serif",
      fontSize: "5vw",
      color: "white",
      textAlign: "center",
      position: "relative",
      top: "1.5vh",
    },
    cont: {
        position: 'relative',
        backgroundColor: "rgb(55 78 102)",
        width: "70vw",
        height: "150vh",
        bottom: '15vw',
    },
    inn: {
        backgroundColor: "white",
        width: "65vw",
        height: "142vh",
    }
  };

export default MarketPlace;
