import {SideNav} from "../temps/SideNav";
import ButtonBuild  from "../temps/buttonBuild";

class PosterPage extends Component {
  //org state
  state = {
    id: "",
    img: "",
    name: "",
    stars: 4,
    price: 0,
    token: false,
    userId: "",
    courseUserId: "",
    user: "",
    hashPass: "",
    errors: [],
    quantity: 1,
    full: [[...cat.small]],

  };

  //mounts after
  async componentDidMount() {
    const { full } = this.state;
    document.getElementById("filter").style.display = "none";
    //var
    const { context } = this.props;
    const auth = context.authenticatedUser;
    const hashPass = context.hashPass;
    const id = window.location.pathname.split("/");
    console.log(id.length);
    console.log(Number(id[2]));
    for (const arr of full) {
      for (const obj of arr) {
        if (obj.id === Number(id[2])) {
            console.log(obj)
          this.setState({
            id: obj.id,
            img: obj.img,
            name: obj.name,
            stars: obj.star,
            price: obj.price,
          });

        }
      }
    }
  }

  //cancel


  //page
  render() {
    const { name, stars,img, price, userId, courseUserId, errors} = this.state;
    let size = true;
